import { Injectable } from '@angular/core';
import { Observable, of } from 'rxjs';

export interface Course {
  id: number;
  name: string;
  description: string;
  imageUrl: string;
}

@Injectable({
  providedIn: 'root'
})
export class CourseService {

  private courses: Course[] = [
    { id: 1, name: 'JavaScript Basics', description: 'Learn the basics of JavaScript',  imageUrl: 'data:image/png;base64,' },
    { id: 2, name: 'Advanced Python', description: 'Master advanced Python concepts',  imageUrl: 'data:image/png;base64,' },
    { id: 3, name: 'Java for Beginners', description: 'Get started with Java programming',  imageUrl: 'data:image/png;base64,' },
    { id: 4, name: 'Web Development with Angular', description: 'Build dynamic web applications with Angular',  imageUrl: 'data:image/jpeg;base64,' }
    
  ];
  constructor() { }

  getCourses(): Observable<Course[]> {
    return of(this.courses);
  }
}
