    { id: 3, name: 'Java for Beginners', description: 'Get started with Java programming', price: 150, imageUrl: 'data:image/png;base64,' },
    { id: 4, name: 'Web Development with Angular', description: 'Build dynamic web applications with Angular', price: 250, imageUrl: 'data:image/jpeg;base64,' }
    
