import { NgIf, NgFor } from '@angular/common';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-student-profile',
  standalone: true,
  imports: [NgIf, NgFor],
  templateUrl: './student-profile.component.html',
  styleUrls: ['./student-profile.component.css']
})
export class StudentProfileComponent implements OnInit {
  student: any;
  recommendedCourses!: any[];

  ngOnInit(): void {
    // Mock student data
    this.student = {
      image: 'data:image/jpeg;base64,/9j/',
      username: 'Sumedh Kedari',
      purchasedCourses: [
        { 
          title: 'JavaScript',
          image: 'data:image/png;base64,',
       completion: 25
        },
        { 
          title: 'Web Development with Angular',
          image: 'data:image/jpeg;base64,',
          completion: 50 
        }
      ]
    };

    this.recommendedCourses = [
      { 
        title: 'React for Beginners',
        image: 'data:image/png;base64,' },
 
      { 
        title: 'Vue.js Essentials',
        image:'data:image/jpeg;base64,' }
  ];
  }

  onImageChange(event: any): void {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e: any) => {
        this.student.image = e.target.result;
      };
      reader.readAsDataURL(file);
    }
  }

  purchaseCourse(course: any): void {
    this.student.purchasedCourses.push({
      title: course.title,
      image: course.image,
      completion: 0
    });
    this.recommendedCourses = this.recommendedCourses.filter(c => c !== course);
  }
}
